mapper.select:
select
id, menu_id, menu_name, to_menu_id, to_menu_name, menu_state, creater, modifier, create_time, modify_time
from conference_apply.menu_info


        and id = #{id}


        and menu_id = #{menuId}


        and menu_name = #{menuName}


        and to_menu_id = #{toMenuId}


        and to_menu_name = #{toMenuName}


        and menu_state = #{menuState}


        and creater = #{creater}


        and modifier = #{modifier}


        and create_time = #{createTime}


        and modify_time = #{modifyTime}
