mapper.select:
select
id, role_id, menu_id, status, creator, modifier, creator_date, update_date
from conference_apply.role_menu


        and id = #{id}


        and role_id = #{roleId}


        and menu_id = #{menuId}


        and status = #{status}


        and creator = #{creator}


        and modifier = #{modifier}


        and creator_date = #{creatorDate}


        and update_date = #{updateDate}
